function InstanceWorkerRunToEatQueue:IsReservedSeat(actor)
    local actorSeatBind = InstanceModel.actorSeatBind[InstanceDataManager.timeType.eat]
    local actorData = actor.data
    if actorSeatBind[actorData.roomID] and actorSeatBind[actorData.roomID][actorData.furnitureIndex] then
        return true
    else
        return false
    end
