---@class CycleCastleWorker:ActorBase
---@field super ActorBase
local CycleCastleWorker = Class("CycleCastleWorker", ActorBase)

function CycleCastleWorker:ctor()
    self.super.ctor(self)
    self.initialized = false
    self.m_type = ActorDefine.ActorType.CycleCastleWorker
    self.m_go = nil
    self.m_animator = nil ---@type UnityEngine.Animator
end

function CycleCastleWorker:Init(id, go, actorData, pos, rot,isBuy)
    self.m_animator = go:GetComponent(typeof(Animator))
    self.data = actorData

    --优化Animator性能
    if not self.m_animator:IsNull() and not self.m_animator.applyRootMotion then
        self.m_animator.cullingMode = CS.UnityEngine.AnimatorCullingMode.CullUpdateTransforms
    end

    if not self.m_stateMachine then
        self.m_stateMachine = PersonStateMachine.create()   ---@type PersonStateMachine
        self.m_stateMachine:SetOwner(self)
        self:TryTransState(function ()
            self.m_stateMachine:ChangeState(ActorDefine.CycleInstanceState.CycleCastleWorkerWorkState, self,isBuy)
        end)
    end
    self.super.Init(self, id, go, actorData, self.m_stateMachine, pos, rot)
    self.data.type = "CycleCastleWorker"
    self.m_go = go
    self.initialized = true
    --self:SetDoorTrigger()
    print("创建 CycleCastleWorker", id)
end

--设置气泡的显示
function CycleCastleWorker:SetTheDisplayOfBubbles(typeB, interactiveRoomId)
    local currentModel = CycleInstanceDataManager:GetCurrentModel()
    local state =
    {
        interactiveRoomId = interactiveRoomId,
        roomId = self.data.roomID,
        index = self.data.furnitureIndex,
        workProgress = 0,
        productionID = currentModel.roomsConfig[self.data.roomID].production
    }
    local speed
    if typeB == "product" then
        local roomCfg = currentModel.roomsConfig[state.roomId]
        local allReduce = currentModel:GetRoomCDReduce(state.roomId)
        local cd = roomCfg.bastCD - allReduce
        speed = 1 / cd
    elseif typeB == "eat" then
        error("CycleCastleWorker在此副本没有 eat 状态")
    elseif typeB == "sleep" then
        error("CycleCastleWorker在此副本没有 sleep 状态")
    end
    state.speed = speed

    self.bubbleState = state
    self.typeB = typeB
    FloatUI:SetObjectCrossCamera(self, function(view)        
        view:Invoke("RefreshCycleCastleWorkerBubble", self.typeB, self.bubbleState)
    end,function()
        if not self.view then
            return
        end
        self.view:Invoke("RefreshCycleCastleWorkerBubble",typeB,state)
    end,0)                   
end

