function CycleNightClubPopUIView:OnExit()
    ShopManager:refreshBuySuccess()
    ShopManager:refreshBuyFail()
    for k, v in pairs(self.timers) do
        GameTimer:StopTimer(v)
    end
    self.super:OnExit(self)
end

return CycleNightClubPopUIView