local RUN_SPEED = 10

---@class CycleToyChildPlay:AIStateBase
---@field m_owner CycleToyWorker
local CycleToyChildPlay = Class("CycleToyChildPlay",AIStateBase)
local instanceScene = nil


function CycleToyChildPlay:ctor()
    self.timer = nil
    self.foodGO = nil
end

function CycleToyChildPlay:OnEnter(...)
    local actor = self.m_owner
    local actorData = actor.data
    local stateMachine = actor.m_stateMachine
    
    self.animator = UIView:GetComp(actor.gameObject, "", "Animator")
    self.isStroll = true
    self.idleTime = 0
    self:Stroll(actor, true)
end

function CycleToyChildPlay:Stroll(actor, initStartPos)
    if not self.isStroll then
        return
    end
    local speed = WALK_SPEED
    local nearestPosGO = CycleToyAIBlackBoard:GetRandomChildPos(actor)
    if not nearestPosGO then
        return
    end
    if initStartPos then
        actor.gameObject.transform.position = nearestPosGO.transform.position
    end
    -- 播放动画
    self.animator:Play("run")
    actor:CalculatePath(
        nearestPosGO.transform.transform,
        true,
        speed,
        Random.Range(1.1, 4.1),
        function()
            local animaName = nearestPosGO.name
            CycleToyAIBlackBoard:RecycleChildPos(actor)

            local idleTime = CycleToyAIBlackBoard:GetRandomChildPlayTime()
            self.timer = GameTimer:CreateNewTimer(0.1, function()
                if self.idleTime > 0 then   -- fix:有的时候随机到的点跟上次的点一样, 这个时候会优先播run动画
                    self.animator:Play(animaName)
                end
                if self.idleTime >= idleTime then
                    self:Stroll(actor)
                    GameTimer:StopTimer(self.timer)
                    self.timer = nil
                    self.idleTime = 0
                else
                    self.idleTime = self.idleTime + 0.1
                end
            end, true)
        end
    )
end

function CycleToyChildPlay:OnExit()
    if self.isStroll then
        CycleToyAIBlackBoard:RecycleStrollPos(self.m_owner)
        self.isStroll = false
    end

    self.idleTime = 0
    if self.timer then
        GameTimer:StopTimer(self.timer)
        self.timer = nil
    end
end
function CycleToyChildPlay:OnDestroy()
    self:OnExit()
end

return CycleToyChildPlay