local WALK_SPEED = 5.5
local RUN_SPEED = 10


---@class CycleToyClientWaitToBuy:AIStateBase
---@field m_owner CycleToyWorker
local CycleToyClientWaitToBuy = Class("CycleToyClientWaitToBuy",AIStateBase)

function CycleToyClientWaitToBuy:ctor()
    self.timer = nil
    self.isStroll = false
end

function CycleToyClientWaitToBuy:OnEnter(...)
    local actor = self.m_owner
    local actorData = actor.data
    local stateMachine = actor.m_stateMachine
    
    self.isStroll = true
    self.idleTime = 0
    
    -- 开始闲逛
    self:Stroll(actor)

    CycleToyAIBlackBoard:EnterBuyQueue(actor)
end

function CycleToyClientWaitToBuy:Stroll(actor)
    if not self.isStroll then
        return
    end
    local speed = WALK_SPEED
    local nearestPosGO = CycleToyAIBlackBoard:GetRandomStrollPos(actor)
    -- 播放动画
    if not actor then
        return
    end
    actor:SetAnimator(PERSON_ACTION.TROLLEY_RUN)
    if not nearestPosGO and not  nearestPosGO.transform then
        return
    end
    actor:CalculatePath(
        nearestPosGO.transform,
        true,
        speed,
        Random.Range(1.1, 4.1),
        function()
            actor:SetAnimator(PERSON_ACTION.TROLLEY_IDLE_EMPTY)
            CycleToyAIBlackBoard:RecycleStrollPos(actor)
            
            local idleTime = CycleToyAIBlackBoard:GetRandomStrollIdleTime()
            self.timer = GameTimer:CreateNewTimer(1, function()
                if self.idleTime >= idleTime then
                    self:Stroll(actor)
                    GameTimer:StopTimer(self.timer)
                    self.timer = nil
                    self.idleTime = 0
                else
                    self.idleTime = self.idleTime + 1
                end
            end, true)
        end
    )
end

function CycleToyClientWaitToBuy:OnExit()
    if self.isStroll then
        CycleToyAIBlackBoard:RecycleStrollPos(self.m_owner)
        self.isStroll = false
    end
    
    self.idleTime = 0
    if self.timer then
        GameTimer:StopTimer(self.timer)
        self.timer = nil
    end
end

function CycleToyClientWaitToBuy:OnDestroy()
    self:OnExit()
end

return CycleToyClientWaitToBuy