---检查是否需要触发第二循环的引导对话
function CycleToyMainViewUIView:CheckSecondCycleGuide()
    local currentModel = CycleInstanceDataManager:GetCurrentModel()
    --1.是否触发过引导
    if currentModel:IsGuideCompleted(CycleInstanceDefine.GuideDefine.Toy.UnlockedSecondCycle) then
        return false
    end
    --2.是否是解锁9号房间
    if not currentModel:RoomIsUnlock(1042) then
        return false
    end
    --3.是否有足够的货币升级一号房间第四级
    local roomLevelCfg = currentModel:GetRoomLevelConfig(1034,4)
    local cost = roomLevelCfg.upgrade_cost or 0
    if BigNumber:CompareBig(cost, currentModel:GetCurInstanceCoin()) then
        return false
    end
    --4.开启引导
    GameUIManager:SetEnableTouch(false)
    GuideManager.currStep = CycleInstanceDefine.GuideDefine.Toy.UnlockedSecondCycle
    GuideManager:ConditionToStart()
    currentModel:SetGuideCompleted(CycleInstanceDefine.GuideDefine.Toy.UnlockedSecondCycle)
    return true
end

return CycleToyMainViewUIView